	else 
		echo ("SELECT ID,MENU_ID,decode(a.URL,'','',a.ip||a.URL) URL,RESOURCES_NAME  FROM(select  ID, MENU_ID,URL,(case when substr(#userReqIp#,1,#lengthSql#) = substr(d.subsystem_ip,1,#lengthSql#)  then subsystem_address   when substr(#userReqIp#,1,#lengthSql#) = substr(d.subsystem_ip2,1,#lengthSql#)  then subsystem_address2 end ) IP,RESOURCES_NAME  from E_USER_COLLECT U,E_MENU M,d_subsystem d where U.MENU_ID=M.RESOURCES_ID  and M.ext1=d.subsystem_id(+) and U.ID in( select ID from E_USER_COLLECT U where U.user_id = #USER_ID#)) A order by ID");


