	and t.result in ('1','2','3') 
	{ and t.sys_from = #sysFrom# }
	{ and t.sys_to = #sysTo# }
	{ and CREATE_TIME >= #startTime+'000000'# }
	{ and CREATE_TIME <= #endTime+'235959'# }
order by create_time desc


